	('s1_p1', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml" lang="en" xml:lang="en">\r\n<head>\r\n      <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\r\n      <title>Tytuł</title>\r\n      <link rel="stylesheet" type="text/css" href="style.css">\r\n      <script src="jquery-3.1.1.min.js"></script>\r\n</head>\r\n\r\n<body>\r\n<script src="https://ajax.googleapis.com/ajax/libs/jquery/3.1.1/jquery.min.js"></script>\r\n<script>\r\n$(\'.smoothscroll\').on(\'click\', function (e) {\r\n	 	\r\n	 	e.preventDefault();\r\n\r\n   	var target = this.hash;        \r\n\r\n    	$(\'html,body\').animate({\r\n          scrollTop: $(target).offset().top\r\n        }, 500, "swing");\r\n      });\r\n\r\n  	}); \r\n</script>\r\n\r\n<div class="nav">\r\n    <a href="#jeden" class="smoothscroll">Aktualności</a>\r\n    <a href="#dwa" class="smoothscroll">Sekcje</a>\r\n    <a href="#trzy" class="smoothscroll">Kadra</a>\r\n    <a href="#cztery" class="smoothscroll">O nas</a>\r\n    <a href="#piec" class="smoothscroll">Kontakt</a>\r\n</div>\r\n\r\n<div class = "content"> \r\n\r\n\r\n    <div class = "spcer" id="jeden">&nbsp;</div>\r\n	<section class="p1">\r\n	<h1>Aktualności</h1>'),
