	('s2_index', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml" lang="en" xml:lang="en">\r\n<head>\r\n      <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\r\n      <title>Tytuł</title>\r\n      <link rel="stylesheet" type="text/css" href="style.css">\r\n</head>\r\n\r\n<body>\r\n<!-- \r\n<div class="nav">\r\n    <a href="#jeden" id="left" class = "nav_btn">Jeden</a>\r\n    <a href="#dwa" id="middle" class = "nav_btn">Dwa</a>\r\n    <a href="#trzy" id="right" class = "nav_btn">Trzy</a>\r\n<div/>\r\n -->\r\n \r\n<div class = "container"> <!-- start całego cointainera -->\r\n    <div  class = "head">\r\n    </div> \r\n    <div  class = "nav">\r\n    <a href="index.php?menu=1" class = "nav_btn">Aktualności</a>\r\n    <a href="index.php?menu=2" class = "nav_btn">Sekcje</a>\r\n    <a href="index.php?menu=3" class = "nav_btn">Kadra</a>  \r\n    <a href="index.php?menu=4" class = "nav_btn">O nas</a>  \r\n    </div>\r\n\r\n    <div id="content" class = "content"> \r\n<?php\r\n\r\nif (!isset($_GET[\'menu\']))\r\n    include(\'1.html\');\r\nelse\r\n{    \r\n    $arg = (int)$_GET[\'menu\'];\r\n     \r\n    switch ($arg)\r\n    {\r\n        case 1:\r\n            include(\'1.html\');\r\n        break;\r\n     \r\n        case 2:\r\n            include(\'2.html\');\r\n        break;\r\n     \r\n        case 3:\r\n            include(\'3.html\');\r\n        break;\r\n        \r\n        case 4:\r\n            include(\'4.html\');\r\n        break;\r\n     \r\n        default:\r\n            include(\'1.html\');\r\n        break;\r\n    }\r\n}\r\n \r\n?>\r\n    </div>\r\n    \r\n    <div id = "content_r" class = "content_right"> \r\n    <?php\r\n    include(\'5.html\');\r\n    ?>\r\n    </div>\r\n    \r\n</div> <!-- koniec całego cointainera -->\r\n\r\n</body>\r\n</html>\r\n'),
