DROP DATABASE IF EXISTS `strona`;
CREATE DATABASE IF NOT EXISTS `strona`;
USE `strona`;


DROP TABLE IF EXISTS `szablony`;
CREATE TABLE IF NOT EXISTS `szablony` (
  `nazwa` varchar(15) NOT NULL,
  `tekst` varchar(5000) NOT NULL,
  PRIMARY KEY (`nazwa`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


